INSERT INTO `category_items` (`id`, `title`, `slug`, `category_id`, `description`, `image`, `status`, `created_at`, `updated_at`) VALUES
(1, 'Du Lịch Hà Nội', 'du-lich-ha-noi', 1, '<p>Du lịch Hà Nội.&nbsp;Hành trình khám phá thủ đô <strong>Hà Nộ</strong>i nơi lưu giữ biết bao tinh hoa của dân tộc, lưu dấu nhiều di tích như <strong>Hồ Gươm</strong>,&nbsp;<strong>Chùa Quán Sứ</strong>, <strong>Hồ Tây</strong>, <strong>36 phố phường</strong>. Hà Nội&nbsp; còn được biết đến bởi những món ăn nổi tiếng của người Hà Thành: <strong>phở, chả cá Lã Vọng, bánh tôm Hồ Tây</strong></p>', 'category_item/FUWQQ7073bN4UCFN5Hjro7BGlvRFNF9DqInXrfv0.jpg', 1, '2022-07-06 07:58:58', '2022-07-10 07:08:23'),
(2, 'Du Lịch Sapa', 'du-lich-sapa', 1, '<p>Du lịch Sapa.&nbsp;Thị trấn trong mây-<strong>Sapa</strong> ẩn chứa bao điều kỳ diệu của thiên nhiên với địa hình của núi đồi, màu xanh của rừng tạo nên một vùng có nhiều cảnh sắc thơ mộng. Núi non hùng vĩ cùng trải nghiệm độc đáo với cuộc sống của đồng bào dân tộc thiểu số luôn&nbsp;hấp dẫn khách du lịch khi đến với <strong>Sapa.</strong></p>', 'category_item/5zhIyipotQT7BckUAarpA983oZ3Ux9aCJs7LDK2C.jpg', 1, '2022-07-06 08:00:49', '2022-07-10 07:09:06'),
(3, 'Du Lịch Hạ Long', 'du-lich-ha-long', 1, '<p>Du lịch Đà Nẵng.&nbsp;Thành phố&nbsp; Đà Nẵng&nbsp;là một trong những điểm du lịch nghỉ dưỡng lý tưởng cho du khách với nhiều danh thắng tuyệt đẹp làm say lòng người như Ngũ Hành Sơn, Bà Nà, bán đảo Sơn Trà, đèo Hải Vân, sông Hàn thơ mộng và biển Mỹ Khê đẹp nhất hành tinh</p>', 'category_item/9CclJxIQ3zZ85DQ4JD8LVzDYv9iTs3Lu2g52rWnD.jpg', 1, '2022-07-06 08:00:55', '2022-07-10 07:13:59'),
(4, 'Du Lịch Đà Nẵng', 'du-lich-da-nang', 3, '<p>Du lịch Đà Nẵng.&nbsp;Thành phố&nbsp; Đà Nẵng&nbsp;là một trong những điểm du lịch nghỉ dưỡng lý tưởng cho du khách với nhiều danh thắng tuyệt đẹp làm say lòng người như Ngũ Hành Sơn, Bà Nà, bán đảo Sơn Trà, đèo Hải Vân, sông Hàn thơ mộng và biển Mỹ Khê đẹp nhất hành tinh</p>', 'category_item/THM3xP0igvqM8Ec7oYhRpKFMkWK8A30UMdDVsSGJ.jpg', 1, '2022-07-06 08:01:06', '2022-07-10 07:15:22'),
(5, 'Du Lịch Đà Lạt', 'du-lich-da-lat', 3, '<p>Du lịch Đà Lạt.&nbsp;Đà Lạt mộng mơ luôn là điểm du lịch thu hút nhiều du khách bởi khí hậu và quang cảnh thiên tuyệt vời. Du khách có thể ngắm nhìn <strong>Hồ Tuyền Lâm</strong>, thăm dinh <strong>Bảo Đại</strong>, <strong>Thiền Viện Trúc Lâm,...</strong>&nbsp;Vườn hoa đua nhau khoe sắc,những chuyến xe ngựa chạy quanh&nbsp;Hồ Xuân Hương cũng là những nét duyên</p>', 'category_item/AKW1octisII1TZAAYWK1t1A0nASV5TjkpeAdviTM.jpg', 1, '2022-07-06 08:01:13', '2022-07-10 07:14:56'),
(6, 'Du Lịch Côn Đảo', 'du-lich-con-dao', 4, '<p>Du lịch Côn Đảo.&nbsp;Ngày nay Côn Đảo được biết đến như địa điểm du lịch biển đảo hấp dẫn du khách trong và ngoài nước. &nbsp;Những bãi biển trong vắt cùng bờ cát trắng mịn làm say mê biêt bao khách dụ lịch khi đặt chân tới Côn Đảo</p>', 'category_item/V4uPLppwyBf2TWqwoMjWtiG6iAwa7Lq429a8bHFK.jpg', 1, '2022-07-06 08:01:25', '2022-07-10 07:17:37'),
(7, 'Du Lịch Phú Quốc', 'du-lich-phu-quoc', 4, '<p>Du lịch Phú Quốc.&nbsp;Phú Quốc là điểm đến dành cho du khách yêu thích hình thức du lịch nghỉ&nbsp;dưỡng và khám phá sinh thái tuyệt vời. Hành trình đến với thiên nhiên và chiêm ngưỡng Mũi Ông Đội, Đá Chào - thế giới san hô và cá biển sặc sỡ, biển Bãi Sao cát trắng mịn, dáng cong, nước xanh ngọc bích.&nbsp;</p>', 'category_item/R6HZ00960iNapqG2fSv28IzYr7GFMzIxShEJPRFD.jpg', 1, '2022-07-06 08:01:33', '2022-07-10 07:16:56'),
(8, 'Du Lịch Hàn Quốc', 'du-lich-han-quoc', 5, '<p>Tour <strong>Hàn Quốc</strong>.&nbsp;<strong>Xứ sở kim chi</strong> - <strong>l</strong>à điểm đến du lịch được nhiều du khách yêu thích với cảnh đẹp và văn hóa tuyệt vời. Du khách có sẽ tham quan nhiều địa danh như: <strong>Blue House&nbsp;</strong>,<strong>đảo Nami&nbsp;</strong>nổi tiếng với bộ phim truyền hình “Bản Tình Ca Mùa Đông”, <strong>đảo ngọc tình yêu Jeju</strong> - nơi dành cho các đôi uyên ương.</p>', 'category_item/vkaROZ1fvoNkpEfzc4bTmGjNJ2RZZitq79EaCLJj.jpg', 1, '2022-07-06 08:01:45', '2022-07-10 07:18:20'),
(9, 'Du Lịch Nhật Bản', 'du-lich-nhat-ban', 5, '<p>Du lịch Nhật Bản. Khám phá một <strong>Tokyo</strong> hiện đại nhưng vẫn cảm nhận từng cung bậc cảm xúc khi dạo bước bên ngôi cổ tự<strong> Asakusa</strong>, <strong>Hoàng cung</strong> và <strong>núi Phú Sĩ </strong>hùng vĩ. Hành trình khám phá cảnh sắc từ <strong>Tokyo</strong> đến <strong>Kyoto</strong>, kết nối <strong>Osaka</strong> với đền đài, chùa thiêng đẫm không khí thiền tịnh và các công trình xuyên biển vĩ đại tại <strong>Kobe.</strong></p>', 'category_item/GdMRaSHm1ynCFj9F2mPRrcOYQeDXtKOUmNeTOzRq.jpg', 1, '2022-07-06 08:01:51', '2022-07-10 07:18:59'),
(10, 'Du Lịch Anh', 'du-lich-anh', 6, '<p>Du lịch <strong>Anh Quốc</strong> - xứ sở sương mù - nơi du khách có thể chiêm ngưỡng thế giới cung điện, lâu đài và cảnh sắc thiên nhiên kỳ thú. Đến tham quan thủ đô <strong>London</strong>,&nbsp; <strong>Stratford-upon-Avon</strong> quê hương của nhà văn vĩ đại Shakespeare, <strong>Oxford </strong>với các trường đại học danh tiếng, và bãi đá cổ <strong>Stonehenge</strong> hơn 3.000 năm</p>', 'category_item/lQFPfdcevuJf81O65mCmbnBjFdhqRfvbDXV2NW4o.jpg', 1, '2022-07-06 08:02:01', '2022-07-10 07:20:14'),
(11, 'Du Lịch SCOTLAND', 'du-lich-scotland', 6, '<p>Du lịch Scotland - vùng đất cổ tích của những người đàn ông mặc váy len Kilt thổi kèn độc đáo. Khám phá thủ đô cổ kính nhưng vô cùng náo nhiệt của Scotland - Edinburgh là một trong những thành phố đẹp nhất Châu Âu với những lâu đài nguy nga.</p>', 'category_item/Y2XOROiEx5yehdEY9Mk2ERyzKfIqiRNMZxEMpEPL.jpg', 1, '2022-07-06 08:02:06', '2022-07-10 07:21:15'),
(12, 'Du Lịch Canada', 'du-lich-canada', 7, '<p>Du lịch Canada.&nbsp;<strong>Canada</strong> đất nước lá phong với bao cảnh đẹp hấp dẫn.&nbsp;Trải nghiệm một Canada năng động và hiện đại vào bậc nhất thế giới với thành phố đa văn hóa, sắc tộc, thanh bình. Chiêm ngưỡng vẻ đẹp của thác <strong>Niagara</strong>, thác <strong>Montmorency</strong>, <strong>Thousand Island</strong> cùng với công viên <strong>Stanley</strong>,...</p>', 'category_item/quYuT4c61JpC5EXCCD7LRheFIEXVSPwlCw69Db60.jpg', 1, '2022-07-06 08:02:15', '2022-07-10 07:22:15'),
(13, 'Du Lịch Mỹ', 'du-lich-my', 7, '<p>Du lịch Mỹ.&nbsp;Chuyến hành trình đến với nước Mỹ đầy sắc màu với những thành phố chứa đựng vô vàn thú vị: Las Vegas - “thành phố không bóng tối”, Los Angeles - “thành phố của các thiên thần”.&nbsp;Cùng Lữ hành Saigontourist&nbsp;đến Hoa Kỳ khám phá nền văn minh hiện đại nhất thế giới.</p>', 'category_item/EO7Hs6RRkJZJioRagq7miy7UVAA3kt2XWRged1zd.jpg', 1, '2022-07-06 08:02:21', '2022-07-10 07:22:54');

